mapper.select:
select do1, count(*) count from tbl_boards where cno = #{cno} and tbno != 0
and place1 like #{place1}
group by do1 order by count desc, do1

select si, dong, count(*) count from tbl_boards where do1 like #{do1} and cno = #{cno} and tbno != 0
and place1 like #{place1}
group by si,dong order by count desc, si
